let payload = (typeof msg.payload === 'string')
    ? JSON.parse(msg.payload)
    : msg.payload;

if (!payload || !payload.data || !payload.data.uplink_message || !payload.data.uplink_message.decoded_payload) {
    node.error("Payload malformado ou incompleto");
    return null;
}

let decoded = payload.data.uplink_message.decoded_payload;
let metadata = payload.data.uplink_message.rx_metadata?.[0];
let location = metadata?.location;

// ðŸ”§ Corrigir formato da data para YYYY-MM-DDTHH:mm:ssZ
let rawDate = payload.data.uplink_message.received_at;
let isoDate = new Date(rawDate).toISOString(); // ex: 2025-06-06T20:26:02Z

let deviceId = payload.data.end_device_ids.device_id;

let sensor = {
    id: `urn:ngsi-ld:Sensor:${deviceId}`,
    type: "Room",

    dateCreated: {
        type: "DateTime",
        value: isoDate
    },
    dateModified: {
        type: "DateTime",
        value: isoDate
    },
    name: {
        type: "Text",
        value: "device-" + deviceId
    },
    description: {
        type: "Text",
        value: `LoRaWAN device ${deviceId} from app ${payload.data.end_device_ids.application_ids.application_id}`
    },
    location: {
        type: "geo:json",
        value: {
            type: "Point",
            coordinates: location
                ? [location.longitude, location.latitude]
                : [-9.142685, 38.736946]
        }
    },
    value: {
        type: "Boolean",
        value: !!decoded.pir_value
    },
    measurement: {
        type: "Number",
        value: decoded.dist_mov_cm ?? 0
    },
    precision: {
        type: "Number",
        value: decoded.batt_percentage ?? 0
    },
    n: {
        type: "Text",
        value: `Tipo alvo: ${decoded.tipo_alvo ?? 'N/A'}`
    },
    range: {
        type: "StructuredValue",
        value: [
            (decoded.dist_mov_cm ?? 0) - 5,
            (decoded.dist_mov_cm ?? 0) + 5
        ]
    },
    step: {
        type: "Number",
        value: 1.0
    },
    rt: {
        type: "StructuredValue",
        value: ["oic.r.sensor"]
    },
    if: {
        type: "StructuredValue",
        value: ["oic.if.s"]
    },
    dataProvider: {
        type: "Text",
        value: "TTN"
    },
    source: {
        type: "Text",
        value: "https://eu1.cloud.thethings.network"
    }
};

msg.payload = sensor;
msg.headers = {}; // ainda podes adicionar os headers aqui se precisares

return msg;

let notification = msg.payload;
let entity = notification.data[0]; // primeiro item do array
let distancia = entity.measurement.value;
let pir = entity.value.value ? 1 : 0;

msg.payload = {
    sensorId: entity.id,
    distancia: distancia,
    pir:pir
};

return msg;

let notification = msg.payload;
let entity = notification.data[0]; // primeiro item do array
let bateria = entity.precision.value;

msg.payload = {
    sensorId: entity.id,
    bateria: bateria
};

return msg;
